mapper.select:
select p.* from t_product p left join t_merchant m on p.supplier_id = m.id left join t_warehouse w on p.preferred_warehouse_id = w.id where p.deleted_ind = 0
and (upper(p.code) like concat('%', upper(#{queryValue}), '%') or upper(p.specification) like concat('%', upper(#{queryValue}), '%') or upper(p.parameter) like concat('%', upper(#{queryValue}), '%') or upper(p.remark) like concat('%', upper(#{queryValue}), '%') or upper(m.name) like concat('%', upper(#{queryValue}), '%') or upper(w.name) like concat('%', upper(#{queryValue}), '%') )
and p.preferred_warehouse_id = #{warehouseId}
